import {useState} from "react";

function Counter(){
    const [count, setCount] = useState(0);
    return(
        <div>
            <h1>{count}</h1>
            <button>Decrease</button>
            <button onClick={() => setCount(count ++)}>Increase</button>
        </div>
    )
}

export default Counter;
